<script>
    export default {
        name: 'Logo',
        data(){
            return{
                delay: 0.1,
                duration: 2,
                strokeWidth: 2,
                timingFunction: 'ease-out',
                strokeColor: '#000000',
                repeat: false
            }
        },
        updated: function(){
            let paths = document.querySelectorAll("path");
            let mode = this.repeat ? 'infinite' : 'forwards';

            for (let i = 0; i < paths.length; i++) {
                const path = paths[i];
                const length = path.getTotalLength();
                path.style["stroke-dashoffset"] = `${length}px`;
                path.style["stroke-dasharray"] = `${length}px`;
                path.style["stroke-width"] = `${this.strokeWidth}px`;
                path.style["stroke"] = `${this.strokeColor}`;
                path.style["animation"] = `${this.duration}s svg-text-anim ${mode} ${this.timingFunction}`;
                path.style["animation-delay"] = `${i * this.delay}s`;
            }
        }
    }
</script>


